# Makefile for Laïcité Project

# Define colors for output
BLUE = \033[0;34m
YELLOW = \033[0;33m
GREEN = \033[0;32m
RED = \033[0;31m
NC = \033[0m # No Color

.PHONY: init-backend
init-backend:
	@echo "${BLUE}Initializing backend...${NC}"

	@echo "${YELLOW}Creating database migrations...${NC}"
	@docker compose exec backend python manage.py makemigrations accounts resources incidents events
	@echo "${GREEN}Migrations created successfully!${NC}"


	@echo "${YELLOW}Applying migrations...${NC}"
	@docker compose exec backend python manage.py migrate
	@echo "${GREEN}Migrations applied successfully!${NC}"

	@echo "${YELLOW}Creating superuser (admin/admin)...${NC}"
	@docker compose exec backend python manage.py createsuperuser --username admin --email admin@example.com --noinput || true
	@docker compose exec backend python manage.py shell -c "from django.contrib.auth import get_user_model; User = get_user_model(); u = User.objects.get(username='admin'); u.set_password('admin'); u.save()"
	@echo "${GREEN}Superuser created/updated!${NC}"

	@echo "${GREEN}Backend initialization completed!${NC}"

.PHONY: migrate
migrate:
	@echo "${YELLOW}Creating database migrations...${NC}"
	@docker compose exec backend python manage.py makemigrations accounts resources incidents events
	@echo "${GREEN}Migrations created successfully!${NC}"

	@echo "${YELLOW}Applying migrations...${NC}"
	@docker compose exec backend python manage.py migrate
	@echo "${GREEN}Migrations applied successfully!${NC}"

.PHONY: createsuperuser
createsuperuser:
	@echo "${YELLOW}Creating superuser...${NC}"
	@docker compose exec backend python manage.py createsuperuser
	@echo "${GREEN}Superuser created successfully!${NC}"

.PHONY: populate-full-dataset
populate-full-dataset:
	@echo "${BLUE}Populating full dataset from article.xml...${NC}"
	@docker compose exec backend python manage.py load_wordpress_xml ./article.xml
	@echo "${GREEN}Full dataset populated successfully!${NC}"

.PHONY: up
up:
	@echo "${BLUE}Starting Docker services...${NC}"
	@docker compose up -d --build
	@echo "${GREEN}Docker services started!${NC}"

.PHONY: down
down:
	@echo "${BLUE}Stopping Docker services...${NC}"
	@docker compose down
	@echo "${GREEN}Docker services stopped!${NC}"

.PHONY: logs
logs:
	@echo "${BLUE}Viewing Docker logs...${NC}"
	@docker compose logs -f
