server {
    listen 80;
    server_name localhost; # Replace with your domain in production

    location / {
        proxy_pass http://frontend;
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection 'upgrade';
        proxy_set_header Host $host;
        proxy_cache_bypass $http_upgrade;
    }

    location /api/ {
        proxy_pass http://backend;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
    }

    location /static/ {
        # This will be for Django's static files
        # In production, these should be collected and served directly by Nginx
        # For now, it can be passed to backend or point to a volume
        proxy_pass http://backend; # or direct to /var/www/static if mounted
        expires 1y;
        add_header Cache-Control "public";
    }

    location /media/ {
        # This will be for Django's media files
        proxy_pass http://backend; # or direct to /var/www/media if mounted
        expires 1y;
        add_header Cache-Control "public";
    }
}

