/**
 * File navigation.js
 *
 * Handles navigation functionality including:
 * - Mobile menu toggle
 * - Search functionality
 * - Accessibility features
 */

( function() {
	// Mobile menu toggle
	const mobileMenuButton = document.querySelector('.mobile-menu-button');
	const mobileNavMenu = document.querySelector('.mobile-nav-menu');

	if (mobileMenuButton && mobileNavMenu) {
		mobileMenuButton.addEventListener('click', function(e) {
			e.preventDefault();

			// Toggle the 'show' class to display/hide mobile menu
			mobileNavMenu.classList.toggle('show');

			// Update ARIA attributes for accessibility
			const isExpanded = mobileMenuButton.getAttribute('aria-expanded') === 'true';
			mobileMenuButton.setAttribute('aria-expanded', !isExpanded);

			// Change the menu icon to X when open
			const menuIcon = mobileMenuButton.querySelector('.menu-icon');
			if (menuIcon) {
				if (mobileNavMenu.classList.contains('show')) {
					// Change to X icon
					menuIcon.innerHTML = `
						<path d="M6 18L18 6M6 6l12 12" stroke="#374151" stroke-width="2" stroke-linecap="round"/>
					`;
				} else {
					// Change back to menu icon
					menuIcon.innerHTML = `
						<path d="M3 12H21M3 6H21M3 18H21" stroke="#374151" stroke-width="2" stroke-linecap="round"/>
					`;
				}
			}
		});
	}

	// Close mobile menu when clicking outside
	document.addEventListener('click', function(event) {
		if (mobileNavMenu && mobileMenuButton) {
			if (!mobileMenuButton.contains(event.target) && !mobileNavMenu.contains(event.target)) {
				mobileNavMenu.classList.remove('show');
				mobileMenuButton.setAttribute('aria-expanded', 'false');

				// Reset menu icon to default
				const menuIcon = mobileMenuButton.querySelector('.menu-icon');
				if (menuIcon) {
					menuIcon.innerHTML = `
						<path d="M3 12H21M3 6H21M3 18H21" stroke="#374151" stroke-width="2" stroke-linecap="round"/>
					`;
				}
			}
		}
	});

	// Close mobile menu on window resize to desktop sizes
	window.addEventListener('resize', function() {
		if (window.innerWidth >= 1024 && mobileNavMenu && mobileMenuButton) {
			mobileNavMenu.classList.remove('show');
			mobileMenuButton.setAttribute('aria-expanded', 'false');

			// Reset menu icon to default
			const menuIcon = mobileMenuButton.querySelector('.menu-icon');
			if (menuIcon) {
				menuIcon.innerHTML = `
					<path d="M3 12H21M3 6H21M3 18H21" stroke="#374151" stroke-width="2" stroke-linecap="round"/>
				`;
			}
		}
	});

	// Search functionality for desktop
	const searchInput = document.querySelector('.search-input');
	if (searchInput) {
		// Handle search form submission
		const searchForm = searchInput.closest('form');
		if (searchForm) {
			searchForm.addEventListener('submit', function(e) {
				e.preventDefault();
				const searchTerm = searchInput.value.trim();
				if (searchTerm) {
					// Redirect to search results page
					window.location.href = `${window.location.origin}/?s=${encodeURIComponent(searchTerm)}`;
				}
			});
		}

		// Allow search on Enter key
		searchInput.addEventListener('keypress', function(e) {
			if (e.key === 'Enter') {
				e.preventDefault();
				const searchTerm = searchInput.value.trim();
