// Force author picker to show in block editor
function observatoire_laicite_enqueue_author_script() {
    if (is_admin() && get_current_screen() && get_current_screen()->post_type === 'post') {
        wp_add_inline_script(
            'wp-edit-post',
            '
            wp.hooks.addFilter(
                "editor.PostPublishPanel",
                "observatoire/show-author-control",
                function(OriginalPostPublishPanel) {
                    return function(props) {
                        return wp.element.createElement(
                            wp.element.Fragment,
                            {},
                            wp.element.createElement(OriginalPostPublishPanel, props)
                        );
                    };
                }
            );

            // Add hook to show author in document sidebar
            wp.domReady(function() {
                wp.data.dispatch("core/edit-post").removeEditorPanel("taxonomy-panel-category");
                wp.data.dispatch("core/edit-post").openGeneralSidebar("edit-post/document");
            });
            '
        );
    }
}
add_action('enqueue_block_editor_assets', 'observatoire_laicite_enqueue_author_script');

/**
 * Limit posts per page to 5 for the Actualit√©s page and archives
 */
function observatoire_laicite_limit_posts_per_page($query) {
    if (!is_admin() && $query->is_main_query()) {
        if (is_home() || is_archive() || is_search()) {
            $query->set('posts_per_page', 5);
        }
    }
}
add_action('pre_get_posts', 'observatoire_laicite_limit_posts_per_page');

